import { Component } from '@angular/core';
import { StockService } from 'src/app/services/stock.service';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  name:string = '';
  stockList = [];
  comapnyName:string = '';
  constructor(private StockService : StockService){

  }

  trackStock(){
    // alert(this.name);
    // this.name='';
    this.StockService.getCompanyName(this.name).subscribe((data:any) => {
      if(data.result.length>0){
        console.log(data.result[0].description,'comapny')
        let comapnyName = data.result[0].description;
        this.getStockQuotedData(comapnyName);
      }
    })


  }

  getStockQuotedData(comapnyName:string){
    this.StockService.getStockQuotedData(this.name).subscribe((data:any) => {
      console.log(data,'data===');
      let obj = {
        companyName : comapnyName,
        changeToday : data.d,
        openingPrice : data.o,
        curentPrice : data.c,
        highPrice  : data.h,
      }

      // if(data.result.length>0){
      //   console.log(data.result[0].description,'comapny')
      //   this.comapnyName = data.result[0].description;
      // }
    })
  }
}
