import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {observable} from 'rxjs'
@Injectable({
  providedIn: 'root'
})
export class StockService {
  token:string='bu4f8kn48v6uehqi3cqg';
  constructor(private http: HttpClient) { }

  getFromLocalStorage(key: string) {
    return localStorage.getItem(key);
  }

  clearStorage() {
    localStorage.clear();
  }

  setToLocalStorage(key: string, value: any) {
    localStorage.setItem(key, value);
  }

  getCompanyName(stockName:string){

    return this.http.get('https://finnhub.io/api/v1/search?q='+stockName + '&token='+this.token);
  }

  getStockQuotedData(stockName:string){
   return this.http.get('https://finnhub.io/api/v1/quote?symbol='+stockName + '&token='+this.token);
  }

  getStockQuotedData(stockName:string){
    return this.http.get('https://finnhub.io/api/v1/stock/insider-sentiment?symbol='+stockName+ '&from=2022-01-01&to=2022-12-01&token='+this.token);
   }
}
